    <span class="token keyword">let</span> <span class="token punctuation">{</span> <span class="token literal-property property">innerWidth</span><span class="token operator">:</span> width<span class="token punctuation">,</span> <span class="token literal-property property">innerHeight</span><span class="token operator">:</span> height <span class="token punctuation">}</span> <span class="token operator">=</span> window<span class="token punctuation">;</span>
