      <span class="token comment">// 计算碰撞后的速度</span>
      <span class="token keyword">let</span> dx <span class="token operator">=</span> arrayBall<span class="token punctuation">.</span>x <span class="token operator">-</span> ball<span class="token punctuation">.</span>x<span class="token punctuation">;</span>
