      <span class="token keyword">let</span> p <span class="token operator">=</span> <span class="token number">2</span> <span class="token operator">*</span> <span class="token punctuation">(</span>ball<span class="token punctuation">.</span>dx <span class="token operator">*</span> nx <span class="token operator">+</span> ball<span class="token punctuation">.</span>dy <span class="token operator">*</span> ny <span class="token operator">-</span> arrayBall<span class="token punctuation">.</span>dx <span class="token operator">*</span> nx <span class="token operator">-</span> arrayBall<span class="token punctuation">.</span>dy <span class="token operator">*</span> ny<span class="token punctuation">)</span> <span class="token operator">/</span> <span class="token punctuation">(</span>ball<span class="token punctuation">.</span>radius <span class="token operator">+</span> arrayBall<span class="token punctuation">.</span>radius<span class="token punctuation">)</span><span class="token punctuation">;</span>
      ball<span class="token punctuation">.</span>dx <span class="token operator">=</span> ball<span class="token punctuation">.</span>dx <span class="token operator">-</span> p <span class="token operator">*</span> arrayBall<span class="token punctuation">.</span>radius <span class="token operator">*</span> nx<span class="token punctuation">;</span>
      ball<span class="token punctuation">.</span>dy <span class="token operator">=</span> ball<span class="token punctuation">.</span>dy <span class="token operator">-</span> p <span class="token operator">*</span> arrayBall<span class="token punctuation">.</span>radius <span class="token operator">*</span> ny<span class="token punctuation">;</span>
      arrayBall<span class="token punctuation">.</span>dx <span class="token operator">=</span> arrayBall<span class="token punctuation">.</span>dx <span class="token operator">+</span> p <span class="token operator">*</span> ball<span class="token punctuation">.</span>radius <span class="token operator">*</span> nx<span class="token punctuation">;</span>
      arrayBall<span class="token punctuation">.</span>dy <span class="token operator">=</span> arrayBall<span class="token punctuation">.</span>dy <span class="token operator">+</span> p <span class="token operator">*</span> ball<span class="token punctuation">.</span>radius <span class="token operator">*</span> ny<span class="token punctuation">;</span>

      <span class="token comment">// 更新小球的位置，使其不重叠</span>
      <span class="token keyword">let</span> overlap <span class="token operator">=</span> ball<span class="token punctuation">.</span>radius <span class="token operator">+</span> arrayBall<span class="token punctuation">.</span>radius <span class="token operator">-</span> <span class="token function">twoCircleDistance</span><span class="token punctuation">(</span>ball<span class="token punctuation">.</span>x<span class="token punctuation">,</span> ball<span class="token punctuation">.</span>y<span class="token punctuation">,</span> arrayBall<span class="token punctuation">.</span>x<span class="token punctuation">,</span> arrayBall<span class="token punctuation">.</span>y<span class="token punctuation">)</span><span class="token punctuation">;</span>
      <span class="token keyword">let</span> offsetX <span class="token operator">=</span> overlap <span class="token operator">*</span> <span class="token punctuation">(</span>ball<span class="token punctuation">.</span>x <span class="token operator">-</span> arrayBall<span class="token punctuation">.</span>x<span class="token punctuation">)</span> <span class="token operator">/</span> <span class="token function">twoCircleDistance</span><span class="token punctuation">(</span>ball<span class="token punctuation">.</span>x<span class="token punctuation">,</span> ball<span class="token punctuation">.</span>y<span class="token punctuation">,</span> arrayBall<span class="token punctuation">.</span>x<span class="token punctuation">,</span> arrayBall<span class="token punctuation">.</span>y<span class="token punctuation">)</span><span class="token punctuation">;</span>
      <span class="token keyword">let</span> offsetY <span class="token operator">=</span> overlap <span class="token operator">*</span> <span class="token punctuation">(</span>ball<span class="token punctuation">.</span>y <span class="token operator">-</span> arrayBall<span class="token punctuation">.</span>y<span class="token punctuation">)</span> <span class="token operator">/</span> <span class="token function">twoCircleDistance</span><span class="token punctuation">(</span>ball<span class="token punctuation">.</span>x<span class="token punctuation">,</span> ball<span class="token punctuation">.</span>y<span class="token punctuation">,</span> arrayBall<span class="token punctuation">.</span>x<span class="token punctuation">,</span> arrayBall<span class="token punctuation">.</span>y<span class="token punctuation">)</span><span class="token punctuation">;</span>
